with (
  filters = {},
  running_jobs = group(slurm_job_info{filters, job_state=~"RUNNING"}) by (job_id, job_name),
  node_fails = 
    (
      # Get nodes with problems: DOWN/ERROR states or drain flag
      slurm_node_info{filters, state_base=~"DOWN|ERROR", state_is_maintenance!="true", node_name=~"$node_name"} or
      slurm_node_info{filters, state_is_drain="true", state_is_maintenance!="true", node_name=~"$node_name"}
    ) * on(node_name) group_left(fail_reason) (
      # Join with failure reasons from counter metric
      label_replace(
        topk by (node_name) (1,
          rate(slurm_node_fails_total{filters, state_is_maintenance!="true", node_name=~"$node_name"}[$__rate_interval])
        ),
        "fail_reason", "$1", "reason", "(.*)"
      ) or on(node_name) (
        # Fallback for nodes without failure counter data
        slurm_node_info{filters, node_name=~"$node_name"} * 0
      )
    ),
    node_fails_with_job_id = node_fails * on (node_name) group_left(job_id) slurm_node_job{filters}
)
node_fails_with_job_id
and on (job_id)
