list.expr:
group by (node, pod) (
  changes(
      sum without (uid, pod_ip)(
          kube_pod_info{pod=~"worker-.*", node!=""}
      )
  ) > 0
  and on (pod) 
  sum by (pod)(
      kube_pod_info{pod=~"worker-.*", node!=""}
  )
)

targets.expr:
sum by (gpu) (
  DCGM_FI_DEV_FB_USED{Hostname=~"$node", exported_pod=~"$worker"}/(DCGM_FI_DEV_FB_USED{Hostname=~"$node", exported_pod=~"$worker"}+DCGM_FI_DEV_FB_FREE{Hostname=~"$node", exported_pod=~"$worker"})
)

node_memory_PageTables_bytes{instance=~"$ip"}
