list.expr:
group by (node, pod) (
  changes(
      sum without (uid, pod_ip)(
          kube_pod_info{pod=~"worker-.*", node!=""}
      )
  ) > 0
  and on (pod) 
  sum by (pod)(
      kube_pod_info{pod=~"worker-.*", node!=""}
  )
)

targets.expr:
avg by (instance, device) (
  1 - 
  (
    node_filesystem_files_free{instance=~"$ip",device!~'none|shm|tmpfs|cloud-metadata|accounting|.*vda15'}
    /
    node_filesystem_files{instance=~"$ip",device!~'none|shm|tmpfs|cloud-metadata|accounting|.*vda15'}
  )
)
* on(instance) group_left(nodename) node_uname_info
* on(nodename) group_left(pod) label_move(kube_pod_info{pod=~"worker.*"}, "node", "nodename")
